sysauto_script.script:
// Get today's time and date
var nowAndThen = new GlideDateTime();

// query the database for NeedIt task records with a due date field with older values than right now
// ONLY return Needit Task records that are not closed complete

gs.info("**== JILL ==**  Right Now = " + nowAndThen);

var due24NITasks = new GlideRecord('x_58872_needit_needit_task');
due24NITasks.addQuery('due_date','>',nowAndThen);
nowAndThen.addSeconds(86400);
gs.info("**== JILL ==**  24 hours from Now = " + nowAndThen);

due24NITasks.addQuery('due_date','<=',nowAndThen);
due24NITasks.addQuery('state','!=',3);  // JILL - why is this a 3?  where could I have seen the display values versus the real values?
due24NITasks.query();



// Write a log message for each of the overdue NeedIt  Task Records

while(due24NITasks.next()){
	gs.info("**== JILL ==**  Due Soon Needit task record = " + due24NITasks.number + " with state of " + due24NITasks.state + " and a due date of " + due24NITasks.due_date);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',due24NITasks,due24NITasks.number,gs.getUserName());
}
